}

#call-to-action .cta-btn {
  text-transform: uppercase;
  font-weight: 500;
  font-size: 16px;
  letter-spacing: 1px;
  display: inline-block;
  padding: 8px 28px;
  border-radius: 8px;
  transition: 0.5s;
  margin-top: 10px;
  border: 2px solid #fff;
