}

#membres-confiance .section-header img {
  height: 220px;
}

@media (max-width: 768px) {
  #membres-confiance .section-header img {
    margin-top: 30px;
  }

  #membres-confiance .section-header .col-md-7 {
    text-align: center;
  }

  #membres-confiance .section-header p {
    text-align: center;
  }
